export function WalletPicker(): JSX.Element {
  const { login } = useAuth();

  const handleLoginFactory = (wallet: Parameters<typeof login>[0]) => () => {
    login(wallet);
  };

  return (
    <LeftBorder>
      <WalletButtons>
        <WalletButton onClick={handleLoginFactory('injected')}>
          {metamask}
          <div>
            Connect with
            <br />
            <b>MetaMask</b>
          </div>
        </WalletButton>

        <WalletButton onClick={handleLoginFactory('walletconnect')}>
          {walletconnect}
          <div>
            Connect with
            <br />
            <b>WalletConnect</b>
          </div>
        </WalletButton>

        <WalletButton onClick={handleLoginFactory('walletlink')}>
          {coinbase}
          <div>
            Connect with
            <br />
            <b>Coinbase Wallet</b>
          </div>
        </WalletButton>

        <WalletButton onClick={handleLoginFactory('ledger')}>
          {ledger}
          <div>
            Connect with
            <br />
            <b>Ledger</b>
          </div>
        </WalletButton>
      </WalletButtons>
    </LeftBorder>
  );
}
